require('dotenv').config();
const express = require('express');
const mongoose = require('mongoose');
const bodyParser = require('body-parser');
const authRoutes = require('./routes/authRoutes');
const app = express();
const port = process.env.PORT || 3000;

mongoose.connect('mongodb://localhost/ai_and_ml_', { useNewUrlParser: true, useUnifiedTopology: true });

app.use(bodyParser.json());
app.use('/api/auth', authRoutes);

app.get('/ping', (req, res) => {
  res.send('pong');
});

app.listen(port, () => {
  console.log(`Server is running on port ${port}`);
}).on('error', (err) => {
  console.error(`Error starting server: ${err.message}`); // INPUT_REQUIRED {Check server error logs if this message appears and take appropriate action}
});
